import streamlit as st

st.title("Hello, Streamlit!")

col1 , col2 = st.columns(2)

with col1:
    st.header("Column 1")
    st.write("This is the first column.")
    if st.button("Press me in Column 1"):
        st.success("Button in Column 1 pressed!")
        st.image("data:image/jpeg;base64,", width=200)

with col2:
    st.header("Column 2")
    st.write("This is the second column.")
    if st.button("Press me in Column 2"):
        st.success("Button in Column 2 pressed!")
        st.image("data:image/jpeg;base64,", width=200)


name = st.sidebar.text_input("Enter your name:")
st.sidebar.write(f"Hello, {name}!")

with st.expander("See explanation"):
    st.write("""
        Here you can put some additional information or instructions.
        This section is collapsible, so users can choose to view it or not.
    """)

st.markdown("---")
st.markdown("### This is a markdown section")
st.markdown("""
You can use **Markdown** to format your text. Here are some examples:
- **Bold text**
- *Italic text* 
- [Link to Streamlit](https://streamlit.io)
""")

